package ch02;

public class Ex02_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(3+2);  // 덧셈 출력하기, 5
		System.out.println(3-2);  // 뺄셈 출력하기, 1
		System.out.println(3*2);  // 곱셈 출력하기, 6
		System.out.println(3/2);  // 나눗셈 출력하기, 1
	}

}
